pragma solidity ^0.4.23;

import "./BasicCrowdsale.sol";
import "./FinalizableCrowdsale.sol";
import "./GrantedCrowdsale.sol";
import "./RefundableCrowdsale.sol";
import "./ReferralCorwdsale.sol";

contract BAXCrowdsale is 
    BasicCrowdsale,
    FinalizableCrowdsale,
    GrantedCrowdsale,
    RefundableCrowdsale,
    ReferralCrowdsale 
{
    constructor(
        uint256 _cliff,
        uint256 _ETH_USD,
        address _wallet,
        address _tokenAddress
        ) 
        public 
    {
        require(_wallet != address(0));
        require(_tokenAddress != address(0));

        uint256 _openingTime = now + _cliff;
        
        ETH_USD = _ETH_USD;

        hardcapTokens = 1e9;
        softcapEth = uint256(3e6).div(ETH_USD);

        minVestingEthPreICO = uint256(50e3).div(ETH_USD).mul(1 ether);
        maxVestingEthPreICO = uint256(500e3).div(ETH_USD).mul(1 ether);
        minVestingEthICO = 0.1 ether;
        maxVestingEthICO = 300 ether;

        addTier(
            _openingTime, 
            _openingTime + 10 days, 
            0, 
            0,
            false, 
            TierStates.preWhitelist);

        addTier(
            Tiers[Tiers.length-1].ending, 
            Tiers[Tiers.length-1].ending + 30 days, 
            uint256(120e6).mul(10 ** 18), 
            ETH_USD.div(8).mul(100),            
            true, 
            TierStates.preICO);

        addTier(
            Tiers[Tiers.length-1].ending, 
            Tiers[Tiers.length-1].ending + 10 days, 
            0, 
            0, 
            false, 
            TierStates.Whitelist);

        addTier(
            Tiers[Tiers.length-1].ending, 
            Tiers[Tiers.length-1].ending + 7 days, 
            uint256(125e6).mul(10 ** 18), 
            ETH_USD.div(12).mul(100), 
            true, 
            TierStates.ICORound1);

        addTier(
            Tiers[Tiers.length-1].ending, 
            Tiers[Tiers.length-1].ending + 7 days, 
            uint256(125e6).mul(10 ** 18), 
            ETH_USD.div(13).mul(100), 
            true, 
            TierStates.ICORound2);

        addTier(
            Tiers[Tiers.length-1].ending, 
            Tiers[Tiers.length-1].ending + 7 days, 
            uint256(125e6).mul(10 ** 18), 
            ETH_USD.div(14).mul(100), 
            true, 
            TierStates.ICORound3);

        addTier(
            Tiers[Tiers.length-1].ending, 
            Tiers[Tiers.length-1].ending + 7 days, 
            uint256(125e6).mul(10 ** 18), 
            ETH_USD.div(15).mul(100), 
            true, 
            TierStates.ICORound4);

        wallet = _wallet;
        state = State.Active;
        isFinalized = false;
        token = baxToken(_tokenAddress);
    }
}
