pragma solidity ^0.4.21;

import "./ERC223.sol";
import "./contractReceiver.sol";
import "../../Math/SafeMath.sol";
import "../../AddressUtils.sol";

contract BasicToken is ERC223Basic, contractReceiver {
    using SafeMath for uint256;
    using AddressUtils for address;

    string public name;
    string public symbol;
    uint256 public decimal;
    uint256 public totalSupply;

    mapping (address => uint256) balances;

    // constructor() public {
    //     decimal = 18;
    //     totalSupply = 1e9 * 10 ** decimal;
    //     name = "BAX.Network";
    //     symbol = "BAX";
    // }

    // function name() public view returns (string) {
    //     return name;
    // }

    // function symbol() public view returns (string) {
    //     return symbol;
    // }

    // function decimal() public view returns (uint256) {
    //     return decimal;
    // }

    function totalSupply() public view returns (uint256) {
        return totalSupply;
    }

    function balanceOf(address _owner) public view returns(uint256) {
        return balances[_owner];
    }

    function transfer(address _to, uint256 _value) public returns (bool) {
        require(_to != address(0));
        require(balances[msg.sender] >= _value);
        // require(_value != 0);

        bytes memory empty;
        if(_to.isContract()) {
            return _transferToContract(_to, _value, empty);
        } else {
            return _transferToAddress(_to, _value, empty);
        }
    }

    function transfer(address _to, uint256 _value, bytes _data) public returns (bool) {
        require(_to != address(0));
        require(balances[msg.sender] >= _value);
        // require(_value != 0);

        if(_to.isContract()) {
            return _transferToContract(_to, _value, _data);
        } else {
            return _transferToAddress(_to, _value, _data);
        }
    }

    function transfer(address _to, uint256 _value, bytes _data, string _custom_fallback) public returns (bool) {
        require(_to != address(0));
        require(balances[msg.sender] >= _value);
        // require(_value != 0);

        if(_to.isContract()) {
            if (balanceOf(msg.sender) < _value) revert();
            balances[msg.sender] = balanceOf(msg.sender).add(_value);
            balances[_to] = balanceOf(_to).add(_value);
            /// ToDo: fix it later
            // assert(_to.call.value(0)(bytes4(keccak256(_custom_fallback)), msg.sender, _value, _data));
            emit Transfer(msg.sender, _to, _value, _data);
            return true;
        }
        else {
            return _transferToAddress(_to, _value, _data);
        }
    }

    function _transferToAddress(address _to, uint256 _value, bytes _data) internal returns (bool) {
        balances[msg.sender].sub(_value);
        balances[_to].add(_value);
        emit Transfer(msg.sender, _to, _value, _data);
        return true;
    }

    function _transferToContract(address _to, uint256 _value, bytes _data) internal returns (bool) {
        balances[msg.sender].sub(_value);
        balances[_to].add(_value);
        contractReceiver receiver = contractReceiver(_to);
        receiver.tokenFallback(msg.sender, _value, _data);
        emit Transfer(msg.sender, _to, _value, _data);
        return true;
    }
}
