pragma solidity ^0.4.23;

library Contributions {
    struct Contribution {
        mapping(address => bool) bearer;
    }

    function add(Contribution storage contribution, address _address) internal {
        contribution.bearer[_address] = true;
    }

    function remove(Contribution storage contribution, address _address) internal {
        contribution.bearer[_address] = false;
    }

    function has(Contribution storage contribution, address _address) internal view returns (bool) {
        return contribution.bearer[_address];
    }

    function check(Contribution storage contribution, address _address) internal view {
        require(has(contribution, _address));
    }

}
