pragma solidity ^0.4.23;

import "../token/ERC223/TimeLockedToken.sol";
import "../Math/SafeMath.sol";
import "./TierCap.sol";
import "./InvestorsWhitelist.sol";
import "./InvestingLimits.sol";
import "./InvestorsWhitelist.sol";
import "./grantLimits.sol";
import "./GranteeWhitelist.sol";

// sould it be closed immediately after softcap reached or not?

contract Crowdsale is Ownable, TierCap, InvestorsWhitelist, InvestingLimits, GranteeWhitelist, grantLimits{
    using SafeMath for uint256;

    enum State {Active, Refunding, Closed}
    State state;
    bool isFinalized;
    event Finalized();
    event Closed();
    event Refunded(address _investor, uint256 _depositedValue);

    TimeLockedToken public token;

    address public wallet;

    uint256 public weiRaised;

    uint256 public tokenSold;

    mapping(address => uint256) depositedWei;

    uint256 public grantedTokens;

    mapping(address => uint256) granted;

    event TokenPurchase(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);
    event TokenGranted(address indexed grantee, uint256 amount);

    constructor(address _wallet, address _tokenAddress) public {
        require(_wallet != address(0));
        require(_tokenAddress != address(0));

        wallet = _wallet;
        state = State.Active;
        isFinalized = false;
        token = TimeLockedToken(_tokenAddress);
    }

    function () external payable {
        buyTokens(msg.sender);
    }

    function grantTokens(address _grantee, uint256 _tokenAmount)
    public
    onlyOwner
    onlyWhieNotClosed {
        require(_grantee != address(0));
        require(_tokenAmount != 0);
        require(getTier() != Rounds.preWhitelist);
        require(getTier() != Rounds.preICO);
        require(whitelistedAsGrantee(_grantee));
        require(granted[_grantee].add(_tokenAmount) <= grantHardcapTokenPerEach); 
        require(grantedTokens.add(_tokenAmount) <= grantHardcapToken);
        // modify this
        token.transferLockedAmount(_grantee, _tokenAmount.mul(3).div(10), endingTime);
        token.transferLockedAmount(_grantee, _tokenAmount.mul(7).div(10), endingTime+365 days);
        emit TokenGranted(_grantee, _tokenAmount);

        granted[_grantee].add(_tokenAmount);
        grantedTokens = tokenSold.add(_tokenAmount);
    }

    function buyTokens(address _beneficiary) 
    public 
    payable
    purchaseAllowed
    onlyWhieNotClosed  {

        uint256 weiAmount = msg.value;
        uint256 tokens = _getTokenAmount(weiAmount);

        _preValidatePurchase(_beneficiary, weiAmount, tokens);

        weiRaised = weiRaised.add(weiAmount);

        _processPurchase(_beneficiary, tokens);
        emit TokenPurchase(msg.sender, _beneficiary, weiAmount, tokens);

        _updatePurchasingState(_beneficiary, weiAmount, tokens);

        // _forwardFunds(); // all Weis go to contract address
        _postValidatePurchase(_beneficiary, weiAmount);
    }

    function _preValidatePurchase(address _beneficiary, uint256 _weiAmount, uint256 _tokenAmount) internal view {
        require(_beneficiary != address(0));
        require(_weiAmount != 0);
        if (getTier() == Rounds.preICO) {
            require(whtielistedAsVC(_beneficiary));
        }
        require(tokenSold.add(_tokenAmount) <= getTierHardcapToken());
        require(depositedWei[_beneficiary].add(_weiAmount) >= minVestingEth());
        require(depositedWei[_beneficiary].add(_weiAmount) <= maxVestingEth());
    }

    function _postValidatePurchase(address _beneficiary, uint256 _weiAmount) internal {
        // optional override
    }

    function _deliverTokens(address _beneficiary, uint256 _tokenAmount) internal {
        token.transfer(_beneficiary, _tokenAmount);
    }

    function _processPurchase(address _beneficiary, uint256 _tokenAmount) internal {
        _deliverTokens(_beneficiary, _tokenAmount);
    }

    function _updatePurchasingState(address _beneficiary, uint256 _weiAmount, uint256 _tokenAmount) internal {
        // optional override
        depositedWei[_beneficiary] = depositedWei[_beneficiary].add(_weiAmount);
        tokenSold = tokenSold.add(_tokenAmount);
    }

    function _getTokenAmount(uint256 _weiAmount) internal view returns (uint256) {
        uint256 roundRate = getTokensPerETH();
        return _weiAmount.mul(roundRate);
    }

    function _forwardFunds() internal {
        wallet.transfer(msg.value);
    }

    function goalReached() public view returns(bool) {
        return weiRaised >= softcapEth;
    }

    function finalize() public onlyOwner {
        require(!isFinalized);
        require(hasClosed());
        finalization();
        emit Finalized();
        isFinalized = true;
    }

    function finalization() internal {
        require(state == State.Active);
        if (goalReached()) {
            state = State.Closed;
            emit Closed();
            wallet.transfer(address(this).balance);
        } else {
            state = State.Refunding;
        }
    }

    function claimRefaund() public {
        require(isFinalized);
        require(state == State.Refunding);
        refund(msg.sender);
    }

    function refund(address _investor) internal {
        require(state == State.Refunding);
        uint256 refundValue = depositedWei[_investor];
        depositedWei[_investor] = 0;
        _investor.transfer(refundValue);
        emit Refunded(_investor, refundValue);
    }
}
