pragma solidity ^0.4.23;

import "./Contributions.sol";
import "../ownership/Ownable.sol";

contract GranteeWhitelist is Ownable {
    using Contributions for Contributions.Contribution;

    Contributions.Contribution Grantees;

    function addToGrantees(address _beneficiary) public onlyOwner {
        Grantees.add(_beneficiary);
    }

    function removeFromGrantees(address _beneficiary) public onlyOwner {
        Grantees.remove(_beneficiary);
    }

    function whitelistedAsGrantee(address _beneficiary) public view returns (bool) {
        Grantees.check(_beneficiary);
    }

    modifier onlyGrantee(address _beneficiary) {
        require(whitelistedAsGrantee(_beneficiary));
        _;
    }

}
