pragma solidity ^0.4.23;

import "./ERC223.sol";
import "../../Math/SafeMath.sol";
import "../../AddressUtils.sol";
import "./contractReceiver.sol";
import "../../ownership/Ownable.sol";

contract StandardToken is ERC223, Ownable {
    using SafeMath for uint256;
    using AddressUtils for address;

    string public name;
    string public symbol;
    uint256 public decimals;
    uint256 public totalSupply;

    mapping (address => uint256) balances;
    mapping (address => mapping(address => uint256)) allowed;

    function name() public view returns (string) {
        return name;
    }

    function symbol() public view returns (string) {
        return symbol;
    }

    function decimals() public view returns (uint256) {
        return decimals;
    }

    function totalSupply() public view returns (uint256) {
        return totalSupply;
    }

    function balanceOf(address _owner) public view returns(uint256) {
        return balances[_owner];
    }

    function transfer(address _to, uint256 _value) public returns (bool) {
        require(_to != address(0));
        require(_value != 0);
        require(_value <= balances[msg.sender]);

        bytes memory empty;
        if(_to.isContract()) {
            return _transferToContract(msg.sender, _to, _value, empty);
        } else {
            return _transferToAddress(msg.sender, _to, _value, empty);
        }
    }

    function transfer(address _to, uint256 _value, bytes _data) public returns (bool) {
        require(_to != address(0));
        require(_value != 0);
        require(_value <= balances[msg.sender]);

        if(_to.isContract()) {
            return _transferToContract(msg.sender, _to, _value, _data);
        } else {
            return _transferToAddress(msg.sender, _to, _value, _data);
        }
    }

    function transfer(address _to, uint256 _value, bytes _data, string _custom_fallback) public returns (bool) {
        require(_to != address(0));
        require(_value != 0);
        require(_value <= balances[msg.sender]);

        if(_to.isContract()) {
            balances[msg.sender] = balances[msg.sender].sub(_value);
            balances[_to] = balances[_to].add(_value);
            assert(_to.call.gas(2300).value(0)(bytes4(keccak256(_custom_fallback)), msg.sender, _value, _data));
            emit Transfer(msg.sender, _to, _value);
            return true;
        }
        else {
            return _transferToAddress(msg.sender, _to, _value, _data);
        }
    }

    function approve(address _spender, uint256 _value) public returns (bool) {
        require(_spender != address(0));
        allowed[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }
    
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
        require(_to != address(0));
        require(_value != 0);
        require(_value <= balances[_from]);
        require(_value <= allowed[_from][msg.sender]);
        
        allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_value);

        bytes memory empty;
        if (_to.isContract()) {
            return _transferToContract(_from, _to, _value, empty);
        } else {

            return _transferToAddress(_from, _to, _value, empty);
        }
    }

    function transferFrom(address _from, address _to, uint256 _value, bytes _data) public returns (bool) {
        require(_to != address(0));
        require(_value != 0);
        require(_value <= balances[_from]);
        require(_value <= allowed[_from][msg.sender]);

        allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_value);

        if (_to.isContract()) {
            return _transferToContract(_from, _to, _value, _data);
        } else {
            return _transferToAddress(_from, _to, _value, _data);
        }
    }

    function transferFrom(address _from, address _to, uint256 _value, bytes _data, string _custom_fallback) public returns (bool) {
        require(_to != address(0));
        require(_value != 0);
        require(_value <= balances[_from]);
        require(_value <= allowed[_from][msg.sender]);

        allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_value);

        if(_to.isContract()) {
            balances[_from] = balances[_from].sub(_value);
            balances[_to] = balances[_to].add(_value);
            assert(_to.call.gas(2300).value(0)(_custom_fallback, msg.sender, _value, _data));
            emit Transfer(_from, _to, _value);
            return true;
        }
        else {
            return _transferToAddress(_from, _to, _value, _data);
        }
    }
    
    function allowance(address _owner, address _spender) public view returns (uint256) {
        return allowed[_owner][_spender];
    }

    function increaseApproval(address _spender, uint256 _addedValue) public returns (bool) {
        require(_spender != address(0));
        allowed[msg.sender][_spender] = allowed[msg.sender][_spender].add(_addedValue);
        emit Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
        return true;
    }

    function decreaseApproval(address _spender, uint256 _subtractedValue) public returns (bool) {
        require(_spender != address(0));
        uint256 oldValue = allowed[msg.sender][_spender];
        if (oldValue <= _subtractedValue) {
            allowed[msg.sender][_spender] = 0;
        } else {
            allowed[msg.sender][_spender] = allowed[msg.sender][_spender].sub(_subtractedValue);
        }
        emit Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
        return true;
    }

    function _transferToAddress(address _from, address _to, uint256 _value, bytes _data) internal returns (bool) {
        balances[_from] = balances[_from].sub(_value);
        balances[_to] = balances[_to].add(_value);
        emit Transfer(_from, _to, _value);
        return true;
    }

    function _transferToContract(address _from, address _to, uint256 _value, bytes _data) internal returns (bool) {
        balances[_from] = balances[_from].sub(_value);
        balances[_to] = balances[_to].add(_value);
        contractReceiver receiver = contractReceiver(_to);
        receiver.tokenFallback(_from, _value, _data);
        emit Transfer(_from, _to, _value);
        return true;
    }
}
