pragma solidity ^0.4.23;

import "./TierPricing.sol";

contract TierCap is TierPricing {
    uint256 public hardcapToken = 1e9;
    uint256 internal softcapEth = ETH_USD.mul(3e6);
    uint256 public preICOherdcapToken = 120e6;
    uint256 public ICOTier1hardcapToken = 125e9;
    uint256 public ICOTier2hardcapToken = 125e9;
    uint256 public ICOTier3hardcapToken = 125e9;
    uint256 public ICOTier4hardcapToken = 125e9;

    function getTierHardcapToken() 
    public 
    view
    onlyWhieNotClosed
    purchaseAllowed
    returns (uint256) {
        Rounds round = getTier();
        if (round == Rounds.preICO) {
            return preICOherdcapToken;
        } else if (round == Rounds.ICORound1) {
            return ICOTier1hardcapToken.add(preICOherdcapToken);
        } else if (round == Rounds.ICORound2) {
            return ICOTier2hardcapToken.add(ICOTier1hardcapToken).add(preICOherdcapToken);
        } else if (round == Rounds.ICORound3) {
            return ICOTier3hardcapToken.add(ICOTier2hardcapToken).add(ICOTier1hardcapToken).add(preICOherdcapToken);
        } else if (round == Rounds.ICORound4) {
            return ICOTier4hardcapToken.add(ICOTier3hardcapToken).add(ICOTier2hardcapToken).add(ICOTier1hardcapToken).add(preICOherdcapToken);
        }
    }

}
